// src/components/RecommendationForm.jsx
import React, { useState } from "react";
import axios from "axios";

const RecommendationForm = () => {
  const [profile, setProfile] = useState({
    age: "",
    annualIncome: "",
    riskAppetite: "Moderate",
    investmentHorizon: "",
    financialGoal: "",
  });
  const [investment, setInvestment] = useState("");
  const [recommendation, setRecommendation] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendation("");

    try {
      const res = await axios.post("http://localhost:5000/api/recommendation", {
        profile,
        investment,
      });
      setRecommendation(res.data.recommendation);
    } catch (err) {
      setRecommendation("❌ Failed to fetch recommendation.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Investment Recommendation</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="number" name="age" placeholder="Age" onChange={handleChange} className="w-full p-2 border rounded" />
        <input type="number" name="annualIncome" placeholder="Annual Income (₹)" onChange={handleChange} className="w-full p-2 border rounded" />
        
        <select name="riskAppetite" onChange={handleChange} className="w-full p-2 border rounded">
          <option value="Low">Low</option>
          <option value="Moderate" selected>Moderate</option>
          <option value="High">High</option>
        </select>

        <input type="number" name="investmentHorizon" placeholder="Investment Horizon (years)" onChange={handleChange} className="w-full p-2 border rounded" />
        <input type="text" name="financialGoal" placeholder="Financial Goal" onChange={handleChange} className="w-full p-2 border rounded" />

        <input type="number" value={investment} onChange={(e) => setInvestment(e.target.value)} placeholder="Investment Amount (₹)" className="w-full p-2 border rounded" />

        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">
          {loading ? "Generating..." : "Get Recommendation"}
        </button>
      </form>

      {recommendation && (
        <div className="mt-6 p-4 bg-gray-100 border rounded whitespace-pre-wrap">
          <strong>Recommendation:</strong>
          <p className="mt-2">{recommendation}</p>
        </div>
      )}
    </div>
  );
};

export default RecommendationForm;
