import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');
  const [recommendation, setRecommendation] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setProfile(res.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      }
    };

    fetchProfile();
  }, []);

  useEffect(() => {
    const fetchRecommendation = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/recommendation/${profile.userId}`);
        setRecommendation(res.data.recommendation);
      } catch (error) {
        console.error("Error fetching recommendation:", error);
      }
    };

    if (profile) {
      fetchRecommendation();
    }
  }, [profile]);

  if (error) return <div>Error: {error}</div>;
  if (!profile) return <div>Loading profile...</div>;

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Welcome, {profile.name}!</h2>
      <p><strong>Monthly Income:</strong> â‚¹{profile.monthlyIncome}</p>
      <p><strong>Monthly Expenses:</strong> â‚¹{profile.monthlyExpenses}</p>
      <p><strong>Risk Appetite:</strong> {profile.riskAppetite}</p>
      <p><strong>Financial Goal:</strong> {profile.financialGoal}</p>
      <p><strong>Investment Horizon:</strong> {profile.investmentHorizon} years</p>

      {recommendation && (
        <div className="mt-6 p-4 border border-gray-300 bg-gray-50 rounded">
          <h3 className="text-xl font-semibold mb-2">ðŸ’¡ Investment Recommendation</h3>
          <p>{recommendation}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
