import React, { useState } from "react";
import axios from "axios";
import { IoMdSend } from "react-icons/io";

const ChatbotWidget = ({ profile }) => {
  const [open, setOpen] = useState(false);
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMsg = { from: "user", text: input };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:5000/api/chatbot", {
        profile,
        message: input,
      });

      const botReply = { from: "bot", text: res.data.reply };
      setMessages((prev) => [...prev, botReply]);
    } catch (err) {
      setMessages((prev) => [...prev, { from: "bot", text: "Error getting response" }]);
    }
  };

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {open && (
        <div className="bg-white w-80 h-96 rounded shadow-lg flex flex-col">
          <div className="bg-blue-600 text-white p-2 rounded-t">ðŸ’¬ Smart Advisor</div>
          <div className="flex-1 overflow-y-auto p-2 space-y-2 text-sm">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`p-2 rounded ${
                  msg.from === "user" ? "bg-blue-100 self-end" : "bg-gray-200 self-start"
                }`}
              >
                {msg.text}
              </div>
            ))}
          </div>
          <div className="p-2 border-t flex">
            <input
              className="flex-1 text-sm p-1 border rounded"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask about your investments..."
            />
            <button onClick={handleSend} className="ml-2 text-blue-600 text-xl">
              <IoMdSend />
            </button>
          </div>
        </div>
      )}

      <button
        className="bg-blue-600 text-white p-3 rounded-full shadow-lg"
        onClick={() => setOpen(!open)}
      >
        ðŸ’¬
      </button>
    </div>
  );
};

export default ChatbotWidget;
