import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log("Profile response:", res.data); // Debug
        setProfile(res.data); // <<====== Make sure you're setting this correctly
      } catch (err) {
        console.error("Error fetching profile:", err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to load profile');
      }
    };

    fetchProfile();
  }, []);

  

  if (error) return <div>Error: {error}</div>;
  if (!profile) return <div>Loading profile...</div>;

  return (
    <div>
      <h2>Welcome, {profile.name}!</h2>
      <p><strong>Monthly Income:</strong> ₹{profile.monthlyIncome}</p>
      <p><strong>Monthly Expenses:</strong> ₹{profile.monthlyExpenses}</p>
      <p><strong>Risk Appetite:</strong> {profile.riskAppetite}</p>
      <p><strong>Financial Goal:</strong> {profile.financialGoal}</p>
      <p><strong>Investment Horizon:</strong> {profile.investmentHorizon} years</p>
    </div>
  );
};

export default Dashboard;
