import React, { useEffect, useState } from "react";
import axios from "axios";
import { PieChart, Pie, Cell, Tooltip } from "recharts";
import { Slider, Typography } from "@mui/material";
import { jwtDecode } from "jwt-decode";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];

const Dashboard = () => {
  const [userId, setUserId] = useState(null);
  const [profile, setProfile] = useState(null);
  const [recommendation, setRecommendation] = useState(null);
  const [investment, setInvestment] = useState(50000);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const decoded = jwtDecode(token); // âœ… Correct usage
      setUserId(decoded.id);
    }
  }, []);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!userId) return;

      try {
       const token = localStorage.getItem("token");
const res = await axios.get("/api/profile", {
  headers: {
    Authorization: `Bearer ${token}`
  }
});

      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };

    fetchProfile();
  }, [userId]);

  useEffect(() => {
    if (profile) {
      generateRecommendation(profile);
    }
  }, [profile, investment]);

  const generateRecommendation = async (profileData) => {
    try {
      const response = await axios.post(`/api/recommendation/${userId}`, {
        ...profileData,
        customInvestment: investment,
      });
      setRecommendation(response.data.recommendation);
    } catch (err) {
      console.error("Error:", err);
    }
  };

  const parsePieChartData = (text) => {
    const match = text.match(/Distribution by pie chart format:\s*(\{.*\})/i);
    if (!match) return [];
    try {
      const json = JSON.parse(match[1]);
      return Object.entries(json).map(([name, value]) => ({ name, value }));
    } catch {
      return [];
    }
  };

  const pieData = recommendation ? parsePieChartData(recommendation) : [];

  return (
    <div className="p-6">
      <h2 className="text-2xl mb-4">Personalized Investment Dashboard</h2>

      <div className="mb-6">
        <Typography gutterBottom>Investment Amount: â‚¹{investment}</Typography>
        <Slider
          value={investment}
          onChange={(e, val) => setInvestment(val)}
          step={1000}
          min={10000}
          max={100000}
          valueLabelDisplay="auto"
        />
      </div>

      {recommendation && (
        <>
          <div className="bg-gray-100 p-4 rounded shadow mb-6 whitespace-pre-wrap">
            {recommendation}
          </div>

          {pieData.length > 0 && (
            <>
              <h3 className="text-xl mb-2">Asset Allocation</h3>
              <PieChart width={400} height={300}>
                <Pie
                  data={pieData}
                  dataKey="value"
                  nameKey="name"
                  cx="50%"
                  cy="50%"
                  outerRadius={100}
                  label
                >
                  {pieData.map((entry, index) => (
                    <Cell key={index} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Dashboard;
