// src/components/Recommendation.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const Recommendation = ({ profile, investment }) => {
  const [recommendation, setRecommendation] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecommendation = async () => {
      try {
        const response = await axios.post("/api/recommendation", profile);

        setRecommendation(response.data.recommendation);
      } catch (err) {
        console.error("Error fetching recommendation:", err);
        setRecommendation("Unable to fetch recommendations.");
      } finally {
        setLoading(false);
      }
    };

    if (profile && investment) {
      fetchRecommendation();
    }
  }, [profile, investment]);

  if (loading) return <p>Loading recommendation...</p>;

  return (
    <div className="p-4 bg-white rounded shadow mt-4">
      <h2 className="text-xl font-bold mb-2">Investment Recommendation</h2>
      <p>{recommendation}</p>
    </div>
  );
};

export default Recommendation;
