import React, { useState } from 'react';
import axios from 'axios';

const ProfileForm = () => {
  const [form, setForm] = useState({
    name: '',
    monthlyIncome: '',
    monthlyExpenses: '',
    riskAppetite: '',
    financialGoal: '',
    investmentHorizon: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      const res = await axios.post(
        'http://localhost:5000/api/profile',
        form,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to save profile');
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Your Financial Profile</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        />

        <input
          type="number"
          name="monthlyIncome"
          placeholder="Monthly Income (₹)"
          value={form.monthlyIncome}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        />

        <input
          type="number"
          name="monthlyExpenses"
          placeholder="Monthly Expenses (₹)"
          value={form.monthlyExpenses}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        />

        <select
          name="riskAppetite"
          value={form.riskAppetite}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        >
          <option value="">Select Risk Appetite</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>

        <input
          type="text"
          name="financialGoal"
          placeholder="Financial Goal (e.g. buy a house)"
          value={form.financialGoal}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        />

        <select
          name="investmentHorizon"
          value={form.investmentHorizon}
          onChange={handleChange}
          required
          className="border p-2 w-full"
        >
          <option value="">Select Investment Horizon</option>
          <option value="short-term">Short Term (0-2 years)</option>
          <option value="medium-term">Medium Term (2-5 years)</option>
          <option value="long-term">Long Term (5+ years)</option>
        </select>

        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">
          Save Profile
        </button>
      </form>
      {message && <p className="mt-2 text-green-600">{message}</p>}
    </div>
  );
};

export default ProfileForm;
