import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setProfile(res.data.profile);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      }
    };

    fetchProfile();
  }, []);

  const getPortfolioAdvice = (risk) => {
    switch (risk.toLowerCase()) {
      case 'low': return '60% Debt, 30% Equity, 10% Gold';
      case 'medium': return '40% Debt, 50% Equity, 10% Gold';
      case 'high': return '20% Debt, 70% Equity, 10% Crypto';
      default: return 'Custom mix needed';
    }
  };

  if (error) return <p className="text-red-600 p-4">{error}</p>;
  if (!profile) return <p className="p-4">Loading profile...</p>;

  const savings = profile.monthlyIncome - profile.monthlyExpenses;

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Welcome, {profile.name} ðŸ‘‹</h2>
      
      <div className="bg-gray-100 p-4 rounded shadow mb-4">
        <p><strong>Monthly Income:</strong> â‚¹{profile.monthlyIncome}</p>
        <p><strong>Monthly Expenses:</strong> â‚¹{profile.monthlyExpenses}</p>
        <p><strong>Monthly Savings:</strong> â‚¹{savings}</p>
      </div>

      <div className="bg-gray-100 p-4 rounded shadow mb-4">
        <p><strong>Financial Goal:</strong> {profile.financialGoal}</p>
        <p><strong>Investment Horizon:</strong> {profile.investmentHorizon} years</p>
        <p><strong>Risk Appetite:</strong> {profile.riskAppetite}</p>
      </div>

      <div className="bg-green-100 p-4 rounded shadow">
        <h3 className="font-semibold text-lg mb-2">ðŸ“Š Suggested Portfolio Allocation</h3>
        <p>{getPortfolioAdvice(profile.riskAppetite)}</p>
      </div>
    </div>
  );
};

export default Dashboard;
