// src/components/ProfileForm.jsx
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProfileForm = () => {
  const [profileData, setProfileData] = useState({
    age: "",
    annualIncome: "",
    riskTolerance: "",
    investmentHorizon: "",
    financialGoals: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setProfileData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:5000/api/profile", profileData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      navigate("/dashboard"); // ✅ go to dashboard after saving profile
    } catch (err) {
      console.error("Profile save failed:", err);
      alert("Could not save profile.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Complete Your Profile</h2>
      <input name="age" placeholder="Age" onChange={handleChange} required />
      <input name="annualIncome" placeholder="Annual Income" onChange={handleChange} required />
      <input name="riskTolerance" placeholder="Risk Tolerance" onChange={handleChange} required />
      <input name="investmentHorizon" placeholder="Investment Horizon" onChange={handleChange} required />
      <input name="financialGoals" placeholder="Financial Goals" onChange={handleChange} required />
      <button type="submit">Save Profile</button>
    </form>
  );
};

export default ProfileForm;
