// src/components/FloatingChatbot.jsx
import React, { useState } from 'react';
import { FaComments, FaTimes } from 'react-icons/fa';

const FloatingChatbot = ({ profile }) => {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([{ from: "bot", text: "Hi! Ask me anything about investments or returns." }]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { from: "user", text: input }];
    setMessages(newMessages);
    setInput("");

    try {
      const res = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          message: input,
          profile: profile, // Send user profile as context
        }),
      });
      const data = await res.json();
      setMessages([...newMessages, { from: "bot", text: data.reply }]);
    } catch (err) {
      setMessages([...newMessages, { from: "bot", text: "❌ Error getting response" }]);
    }
  };

  return (
    <div className="fixed bottom-6 right-6 z-50">
      {open ? (
        <div className="bg-white shadow-xl rounded-lg w-80 h-96 flex flex-col">
          <div className="bg-blue-600 text-white px-4 py-2 flex justify-between items-center rounded-t-lg">
            <span>SmartBot</span>
            <button onClick={() => setOpen(false)}><FaTimes /></button>
          </div>
          <div className="flex-1 overflow-y-auto p-2 text-sm space-y-2">
            {messages.map((msg, i) => (
              <div key={i} className={`text-${msg.from === "user" ? "right" : "left"}`}>
                <div className={`inline-block px-3 py-2 rounded ${msg.from === "user" ? "bg-blue-100" : "bg-gray-100"}`}>
                  {msg.text}
                </div>
              </div>
            ))}
          </div>
          <div className="p-2 border-t flex gap-2">
            <input
              className="border flex-1 rounded px-2 py-1 text-sm"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              placeholder="Ask your question..."
            />
            <button className="bg-blue-600 text-white px-3 py-1 rounded text-sm" onClick={sendMessage}>
              Send
            </button>
          </div>
        </div>
      ) : (
        <button
          className="bg-blue-600 text-white p-4 rounded-full shadow-lg hover:bg-blue-700"
          onClick={() => setOpen(true)}
        >
          <FaComments />
        </button>
      )}
    </div>
  );
};

export default FloatingChatbot;
